import Master from './../../layouts/Master'
import React from "react";
import Link from "next/link"
import fetch from 'isomorphic-unfetch';
import Router from 'next/router';

import './../../static/sass/pages/article.scss'

class Article extends React.Component {
    static async getInitialProps({query}) {
        let page = 1;
        if (query.page) page = query.page;
        const res = await fetch(`http://codethue.abc/api/article/list?page=${page}`);
        const articles = await res.json();

        return {
            data  : articles.data,
            total : articles.total
        };
    }
    render() {
        return (
            <Master>
                <section className="section-content padding-y">
                    <div className="container">
                        <div className="row">
                            <div className="col-sm-9">
                                <div className="list_article">
                                    <div className="title_heading">
                                        <h1>Danh sách bài viết</h1>
                                        <p>Có tổng số {this.props.total} bài viết</p>
                                    </div>
                                    {this.props.data.map((article,key) => (
                                        <div className="list_article_item" key={key}>
                                            <div className="item_content">
                                                <h3><a href="">{article.a_name}</a></h3>
                                                <p>{article.a_description}</p>
                                                <span>{article.created_at} - 116 lượt xem</span>
                                            </div>
                                            <Link href="">
                                                <a className="item_avatar">
                                                    <img src={ article.a_avatar}  onerror="this.onError=null;this.src='https://placeimg.com/200/300/animals';" alt={article.a_name} />
                                                </a>
                                            </Link>
                                        </div>            
                                    ))}
                                    <div className="paginate">
                                        <ul>
                                            <li>
                                                <Link href=''>
                                                    <a onClick={() => Router.push(`/bai-viet?page=${this.props.page - 1}`)}>Trang trước</a>
                                                </Link>
                                            </li>
                                            <li>
                                                <Link href=''>
                                                    <a onClick={() => Router.push(`/bai-viet?page=${this.props.page - 1}`)}>Trang trước</a>
                                                </Link>
                                            </li>
                                        </ul>
                                    </div>    
                                </div>
                            </div>   
                            <div className="col-sm-3">

                            </div>   
                        </div>
                    </div>
                </section>
            </Master>
        )
    }
}
export default Article;
